mapper.select:
select 

from sys_resource
where is_deleted = '0'

  and parent_sr_id = #{parentSrId,jdbcType=VARCHAR}


  and resource_type = #{resourceType,jdbcType=VARCHAR}


  and auth_type = #{authType,jdbcType=VARCHAR}


  and resource_code = #{resourceCode,jdbcType=VARCHAR}


  and resource_name like concat(concat('%',#{resourceName,jdbcType=VARCHAR}),'%')


  and url = #{url,jdbcType=VARCHAR}


  and description = #{description,jdbcType=VARCHAR}


  and icon = #{icon,jdbcType=VARCHAR}


  and priority = #{priority,jdbcType=INTEGER}


  and is_check = #{isCheck,jdbcType=BIT},


  and is_available = #{isAvailable,jdbcType=BIT}


  and create_by = #{createBy,jdbcType=VARCHAR}


  and str_to_date(create_time, '%Y-%m-%d') >= str_to_date(#{startTime,jdbcType=TIMESTAMP}, '%Y-%m-%d') 


  and str_to_date(create_time, '%Y-%m-%d') <= str_to_date(#{endTime,jdbcType=TIMESTAMP}, '%Y-%m-%d') 


  order by ${orderBy}


  order by priority
