mapper.select:
select 

from sys_role
where is_deleted = '0'

   and role_code = #{roleCode,jdbcType=VARCHAR}


   and role_name like concat(concat('%',#{roleName,jdbcType=VARCHAR}),'%')  


   and description = #{description,jdbcType=VARCHAR}


   and is_original_role = #{isOriginalRole,jdbcType=BIT}


   and is_show = #{isShow,jdbcType=BIT}


   and is_need_org = #{isNeedOrg,jdbcType=BIT}


   and create_by = #{createBy,jdbcType=VARCHAR}


   and str_to_date(create_time, '%Y-%m-%d') >= str_to_date(#{startTime,jdbcType=TIMESTAMP}, '%Y-%m-%d') 


   and str_to_date(create_time, '%Y-%m-%d') <= str_to_date(#{endTime,jdbcType=TIMESTAMP}, '%Y-%m-%d') 


   order by ${orderBy}


   order by priority
